package kr.ac.skuniv.vo;


public class LocationXY {

	private double x;
	private double y;
	
	public LocationXY(double x , double y) {
		// TODO Auto-generated constructor stub
		
		this.x = x;
		this.y = y;
	}

	public double getX() {
		return x;
	}

	public void setX(double x) {
		this.x = x;
	}

	public double getY() {
		return y;
	}

	public void setY(double y) {
		this.y = y;
	}
	
	
	
}
